import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
@Injectable({
  providedIn: 'root'
})
export class BlogService {

  constructor(private http: HttpClient) { }
  getListOfBlog() {
    return [
      {
        id: 1,
        title: 'blog1',
        description: 'test technique on angular for create a new project of blog i create this for learn angular',
        author: 'Elya data',
        like: 10,
        dislike: 14
      },
      {
        id: 2,
        title: 'blog2',
        description: 'test technique on angular for create a new project of blog i create this for learn angular',
        author: 'mohamed amine',
        like: 20,
        dislike: 14
      },
      {
        id: 3,
        title: 'blog3',
        description: 'test technique on angular for create a new project of blog i create this for learn angular',
        author: 'Cecilia ',
        like: 40,
        dislike: 14
      },
      {
        id: 4,
        title: 'blog4',
        description: 'test technique on angular for create a new project of blog i create this for learn angular',
        author: 'Michael jackson',
        like: 5,
        dislike: 14
      },
      {
        id: 5,
        title: 'blog5',
        description: 'test technique on angular for create a new project of blog i create this for learn angular',
        author: 'Bitcoin',
        like: 16,
        dislike: 14
      }
    ]
  }
  getBlogs() {
    return this.http.get<any[]>('http://localhost:3000/api/getBlogs');
  }
  getBlogById(value: number) {
    let list = this.getListOfBlog();
    let blogExiste = list.filter(blog => {
      return blog.id === value;
    });
    if (blogExiste.length > 0)
      return blogExiste[0];
    else
      return null;

  }

}
