import { Component } from '@angular/core';
import { BlogService } from '../blog.service';


@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
export class BlogComponent {
  title = 'list of blogs'
  listBlog: any[] | undefined;
  currentId: number = -1;
  openBlog(value: number) {
    this.currentId = value;
  }
  constructor(blog: BlogService) {
    this.listBlog = blog.getListOfBlog();
    let blogCurrent = blog.getBlogById(this.currentId);
    console.log(blogCurrent)
  }

}
